function changeTab(t,e){e.forEach(e=>{t.target==e?(e.classList.add("active"),$(`#${e.dataset.target}`)[0].style.display="block"):(e.classList.remove("active"),$(`#${e.dataset.target}`)[0].style.display="none")})}$(".select2-multiple").select2({placeholder:"---",allowClear:!0});const toastElList=document.querySelectorAll(".toast"),toastList=[...toastElList].map(t=>new bootstrap.Toast(t));let bookNav=document.querySelector(".nav-tabs.book-tabs");if(bookNav){let t=bookNav.querySelectorAll(".nav-link");t.forEach(e=>{e.addEventListener("click",e=>changeTab(e,t))})}