function changeTab(e,a){a.forEach(a=>{e.target==a?(a.classList.add("active"),$(`#${a.dataset.target}`)[0].style.display="block"):(a.classList.remove("active"),$(`#${a.dataset.target}`)[0].style.display="none")})}$(".select2-multiple").select2({placeholder:"---",allowClear:!0});let bookNav=document.querySelector(".nav-tabs.book-tabs");if(bookNav){let e=bookNav.querySelectorAll(".nav-link");e.forEach(a=>{a.addEventListener("click",a=>changeTab(a,e))})}